mapper.update:
UPDATE 
	TBL_USER
SET
	USER_PW = #{userPw}
WHERE
	USER_NO = #{userNo}

UPDATE 
	TBL_USER
SET
		USER_NAME = #{userName}
	,	STATE = #{state}
WHERE
	USER_NO  = #{userNo}

mapper.select:
select * from( select rownum rnum, REG_NUM, USER_NO, USER_NAME, STATE from ( select REG_NUM, USER_NO, USER_NAME, STATE from TBL_USER where REG_NUM > 0
order by REG_NUM desc, USER_NO desc
)) where (rnum >= #{pageStart} AND rnum <= #{pageEnd}) order by REG_NUM desc, USER_NO desc
